import { useState, useEffect } from "react";

const initialState = {
  titulo: "",
  autor: "",
  anio: "",
  genero: "",
  cantidad: 0,
};

export default function LibroForm({ onSubmit, libroEditado }) {
  const [form, setForm] = useState(initialState);

  useEffect(() => {
    if (libroEditado) setForm(libroEditado);
    else setForm(initialState);
  }, [libroEditado]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === "cantidad" || name === "anio" ? Number(value) : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(form);
    setForm(initialState);
  };

  return (
    <div className="card shadow-sm mb-4">
      <div className="card-header bg-dark text-white fw-semibold">
        {libroEditado ? "‚úèÔ∏è Editar libro" : "üìö Registrar nuevo libro"}
      </div>
      <div className="card-body bg-light-subtle">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">T√≠tulo</label>
            <input
              className="form-control"
              name="titulo"
              placeholder="Ej: Harry Potter"
              value={form.titulo}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Autor</label>
            <input
              className="form-control"
              name="autor"
              placeholder="Ej: J.K. Rowling"
              value={form.autor}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">A√±o de publicaci√≥n</label>
            <input
              className="form-control"
              name="anio"
              placeholder="Ej: 1997"
              type="number"
              value={form.anio}
              onChange={handleChange}
            />
          </div>
          <div className="mb-3">
            <label className="form-label">G√©nero</label>
            <input
              className="form-control"
              name="genero"
              placeholder="Ej: Fantas√≠a"
              value={form.genero}
              onChange={handleChange}
            />
          </div>
          <div className="mb-4">
            <label className="form-label">Cantidad disponible</label>
            <input
              className="form-control"
              name="cantidad"
              placeholder="Ej: 5"
              type="number"
              value={form.cantidad}
              onChange={handleChange}
              min={0}
              required
            />
          </div>
          <button className="btn btn-success w-100" type="submit">
            {libroEditado ? "üíæ Guardar cambios" : "‚ûï Registrar libro"}
          </button>
        </form>
      </div>
    </div>
  );
}
