// src/components/LibroForm.jsx
import { useState, useEffect } from "react";

const initialState = {
  titulo: "",
  autor: "",
  anio: "",
  genero: "",
  cantidad: 0,
};

export default function LibroForm({ onSubmit, libroEditado }) {
  const [form, setForm] = useState(initialState);

  useEffect(() => {
    if (libroEditado) setForm(libroEditado);
    else setForm(initialState);
  }, [libroEditado]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === "cantidad" || name === "anio" ? Number(value) : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(form);
    setForm(initialState);
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="mb-5 p-4 border rounded shadow-sm bg-light"
    >
      <h4 className="mb-3 text-primary fw-bold">
        {libroEditado ? "✏️ Editar libro" : "📚 Agregar nuevo libro"}
      </h4>

      <div className="row g-3">
        <div className="col-md-6">
          <label className="form-label">Título</label>
          <input
            type="text"
            name="titulo"
            className="form-control"
            value={form.titulo}
            onChange={handleChange}
            required
          />
        </div>

        <div className="col-md-6">
          <label className="form-label">Autor</label>
          <input
            type="text"
            name="autor"
            className="form-control"
            value={form.autor}
            onChange={handleChange}
            required
          />
        </div>

        <div className="col-md-4">
          <label className="form-label">Año de publicación</label>
          <input
            type="number"
            name="anio"
            className="form-control"
            value={form.anio}
            onChange={handleChange}
            min="0"
            max="2100"
          />
        </div>

        <div className="col-md-4">
          <label className="form-label">Género</label>
          <input
            type="text"
            name="genero"
            className="form-control"
            value={form.genero}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4">
          <label className="form-label">Cantidad</label>
          <input
            type="number"
            name="cantidad"
            className="form-control"
            value={form.cantidad}
            onChange={handleChange}
            min="0"
            required
          />
        </div>
      </div>

      <div className="mt-4 text-end">
        <button className="btn btn-success px-4" type="submit">
          💾 Guardar libro
        </button>
      </div>
    </form>
  );
}
