// src/components/LibroForm.jsx
import { useState, useEffect } from "react";

const initialState = {
  titulo: "",
  autor: "",
  anio: "",
  genero: "",
  cantidad: 0,
};

export default function LibroForm({ onSubmit, libroEditado }) {
  const [form, setForm] = useState(initialState);

  useEffect(() => {
    if (libroEditado) setForm(libroEditado);
    else setForm(initialState);
  }, [libroEditado]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === "cantidad" || name === "anio" ? Number(value) : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(form);
    setForm(initialState);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        className="form-control mb-2"
        name="titulo"
        placeholder="Título"
        value={form.titulo}
        onChange={handleChange}
        required
      />
      <input
        className="form-control mb-2"
        name="autor"
        placeholder="Autor"
        value={form.autor}
        onChange={handleChange}
        required
      />
      <input
        className="form-control mb-2"
        name="anio"
        placeholder="Año de publicación"
        type="number"
        value={form.anio}
        onChange={handleChange}
      />
      <input
        className="form-control mb-2"
        name="genero"
        placeholder="Género"
        value={form.genero}
        onChange={handleChange}
      />
      <input
        className="form-control mb-3"
        name="cantidad"
        placeholder="Cantidad disponible"
        type="number"
        value={form.cantidad}
        onChange={handleChange}
        min={0}
        required
      />

      <button className="btn btn-success w-100" type="submit">
        {libroEditado ? "Guardar cambios" : "Registrar libro"}
      </button>
    </form>
  );
}
