// src/components/LibroForm.jsx
import { useState, useEffect } from "react";

const initialState = {
  titulo: "",
  autor: "",
  anio: "",
  genero: "",
  cantidad: 0,
};

export default function LibroForm({ onSubmit, libroEditado }) {
  const [form, setForm] = useState(initialState);

  useEffect(() => {
    if (libroEditado) setForm(libroEditado);
    else setForm(initialState);
  }, [libroEditado]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === "cantidad" || name === "anio" ? Number(value) : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(form);
    setForm(initialState);
  };

  return (
    <div className="card mb-4 shadow-sm border-0">
      <div className="card-header bg-primary text-white fw-semibold">
        {libroEditado ? "‚úèÔ∏è Editar libro" : "üìö Registrar nuevo libro"}
      </div>
      <div className="card-body">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">T√≠tulo</label>
            <input
              type="text"
              name="titulo"
              className="form-control"
              value={form.titulo}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Autor</label>
            <input
              type="text"
              name="autor"
              className="form-control"
              value={form.autor}
              onChange={handleChange}
              required
            />
          </div>

          <div className="row">
            <div className="col-md-4 mb-3">
              <label className="form-label">A√±o</label>
              <input
                type="number"
                name="anio"
                className="form-control"
                value={form.anio}
                onChange={handleChange}
                min="0"
              />
            </div>

            <div className="col-md-4 mb-3">
              <label className="form-label">G√©nero</label>
              <input
                type="text"
                name="genero"
                className="form-control"
                value={form.genero}
                onChange={handleChange}
              />
            </div>

            <div className="col-md-4 mb-3">
              <label className="form-label">Cantidad</label>
              <input
                type="number"
                name="cantidad"
                className="form-control"
                value={form.cantidad}
                onChange={handleChange}
                min="0"
                required
              />
            </div>
          </div>

          <div className="text-end">
            <button type="submit" className="btn btn-success">
              {libroEditado ? "Actualizar" : "Guardar"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
