import { useState, useEffect } from "react";

const initialState = {
  titulo: "",
  autor: "",
  anio: "",
  genero: "",
  cantidad: 0,
};

export default function LibroForm({ onSubmit, libroEditado }) {
  const [form, setForm] = useState(initialState);

  useEffect(() => {
    if (libroEditado) setForm(libroEditado);
    else setForm(initialState);
  }, [libroEditado]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === "cantidad" || name === "anio" ? Number(value) : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(form);
    setForm(initialState);
  };

  return (
    <div className="card shadow-sm mb-4">
      <div className="card-body bg-light-subtle">
        <h5 className="mb-4 fw-semibold text-primary">
          {libroEditado ? "✏️ Editar libro" : "📘 Registrar nuevo libro"}
        </h5>

        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Título</label>
            <input
              className="form-control"
              name="titulo"
              placeholder="Ej. Cien años de soledad"
              value={form.titulo}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Autor</label>
            <input
              className="form-control"
              name="autor"
              placeholder="Ej. Gabriel García Márquez"
              value={form.autor}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Año de publicación</label>
            <input
              className="form-control"
              name="anio"
              type="number"
              placeholder="Ej. 1967"
              value={form.anio}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Género</label>
            <input
              className="form-control"
              name="genero"
              placeholder="Ej. Fantasía, Terror, Ciencia ficción..."
              value={form.genero}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Cantidad disponible</label>
            <input
              className="form-control"
              name="cantidad"
              type="number"
              min={0}
              placeholder="Ej. 5"
              value={form.cantidad}
              onChange={handleChange}
              required
            />
          </div>

          <button className="btn btn-success w-100" type="submit">
            Guardar
          </button>
        </form>
      </div>
    </div>
  );
}
